const API_BASE = '/api';

// Get token from localStorage
const getToken = () => {
  return localStorage.getItem('token');
};

// Get headers with authentication
const getAuthHeaders = () => {
  const token = getToken();
  return {
    'Content-Type': 'application/json',
    ...(token && { 'Authorization': `Bearer ${token}` })
  };
};

export const fetchAppointments = async (startDate, endDate) => {
  const params = new URLSearchParams();
  if (startDate) params.append('startDate', startDate);
  if (endDate) params.append('endDate', endDate);

  const response = await fetch(`${API_BASE}/jobs?${params}`, {
    headers: getAuthHeaders()
  });

  if (!response.ok) {
    throw new Error('Failed to fetch appointments');
  }

  const result = await response.json();
  return result.data;
};

export const createAppointment = async (appointmentData) => {
  const response = await fetch(`${API_BASE}/jobs`, {
    method: 'POST',
    headers: getAuthHeaders(),
    body: JSON.stringify(appointmentData)
  });

  const result = await response.json();

  if (!response.ok) {
    throw new Error(result.message || 'Failed to create appointment');
  }

  return result.data;
};

export const updateAppointment = async (id, appointmentData) => {
  const response = await fetch(`${API_BASE}/jobs/${id}`, {
    method: 'PUT',
    headers: getAuthHeaders(),
    body: JSON.stringify(appointmentData)
  });

  const result = await response.json();

  if (!response.ok) {
    throw new Error(result.message || 'Failed to update appointment');
  }

  return result.data;
};

export const deleteAppointment = async (id) => {
  const response = await fetch(`${API_BASE}/jobs/${id}`, {
    method: 'DELETE',
    headers: getAuthHeaders()
  });

  const result = await response.json();

  if (!response.ok) {
    throw new Error(result.message || 'Failed to delete appointment');
  }

  return result.data;
};

export const fetchTrash = async () => {
  const response = await fetch(`${API_BASE}/jobs/trash`, {
    headers: getAuthHeaders()
  });

  if (!response.ok) {
    throw new Error('Failed to fetch trash');
  }

  const result = await response.json();
  return result.data;
};

export const restoreAppointment = async (id) => {
  const response = await fetch(`${API_BASE}/jobs/${id}/restore`, {
    method: 'POST',
    headers: getAuthHeaders()
  });

  const result = await response.json();

  if (!response.ok) {
    throw new Error(result.message || 'Failed to restore appointment');
  }

  return result.data;
};

