import { useState, useEffect, useMemo } from 'react';
import { fetchAppointments } from '../services/api';
import { fetchHebcalData, getCachedMonth, setCachedMonth } from '../services/hebcal';

const useCalendarData = (year, month, refreshTrigger = 0) => {
  const [appointments, setAppointments] = useState([]);
  const [hebcalEvents, setHebcalEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch data on mount, when month changes, or when refreshTrigger changes
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        // Calculate date range for the month
        const startDate = new Date(year, month, 1).toISOString();
        const endDate = new Date(year, month + 1, 0, 23, 59, 59).toISOString();

        // Fetch appointments from backend
        const appointmentsData = await fetchAppointments(startDate, endDate);

        // Fetch Hebcal data (check cache first)
        const cacheKey = `${year}-${month}`;
        let hebcalData = getCachedMonth(cacheKey);

        if (!hebcalData) {
          hebcalData = await fetchHebcalData(year, month);
          setCachedMonth(cacheKey, hebcalData);
        }

        setAppointments(appointmentsData);
        setHebcalEvents(hebcalData);
      } catch (err) {
        setError(err.message);
        console.error('Error fetching calendar data:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [year, month, refreshTrigger]);

  // Merge and group data by date (YYYY-MM-DD)
  const calendarData = useMemo(() => {
    const grouped = {};

    // Group appointments by date
    appointments.forEach(apt => {
      const dateKey = new Date(apt.date).toISOString().split('T')[0];
      if (!grouped[dateKey]) {
        grouped[dateKey] = { appointments: [], holidays: [], shabbat: null, parasha: null };
      }
      grouped[dateKey].appointments.push(apt);
    });

    // Group Hebcal events by date
    hebcalEvents.forEach(event => {
      const dateKey = event.date.split('T')[0]; // ISO format

      if (!grouped[dateKey]) {
        grouped[dateKey] = { appointments: [], holidays: [], shabbat: null, parasha: null };
      }

      // CRITICAL: Use event.hebrew for Hebrew text, NOT event.title
      if (event.category === 'holiday') {
        grouped[dateKey].holidays.push({
          text: event.hebrew, // HEBREW TEXT
          category: event.category
        });
      } else if (event.category === 'parashat') {
        // Weekly Torah portion (Parashat HaShavua)
        grouped[dateKey].parasha = {
          text: event.hebrew, // HEBREW TEXT
          category: event.category
        };
      } else if (event.category === 'candles' || event.category === 'havdalah') {
        // Shabbat candle lighting or Havdalah
        grouped[dateKey].shabbat = {
          text: event.hebrew, // HEBREW TEXT
          time: event.time
        };
      }
    });

    return grouped;
  }, [appointments, hebcalEvents]);

  return { calendarData, loading, error };
};

export default useCalendarData;

