  const datesWithAppointments = Object.keys(calendarData)
    .filter(dateKey => {
      const date = new Date(dateKey);
      return date.getFullYear() === year &&
             date.getMonth() === month &&
             calendarData[dateKey].appointments.length > 0;
    })
    .sort((a, b) => new Date(a) - new Date(b));

  const handleAppointmentClick = (dateKey) => {
    const dayData = calendarData[dateKey];
    setSelectedDate({ date: dateKey, ...dayData });
  };

  const handleCloseModal = () => {
    setSelectedDate(null);
